INSERT INTO award (winnerName, winnerBackground, winnerSpeech, winnerImage, category_id)
VALUES ('Tom', 'Year 3', 'I saw some hanging baskets on our inspriation walk in the summer time. I wanted to adapt this to be more Christmassy so decided to make it a holly hanging basket! I''d feel very happy and relieved to be a winner!', 'https://git.cardiff.ac.uk/c23042881/sohoawardimg/-/blob/main/.idea/2022/Year%203%20Xmas%20Kids%20entries%202022%203_page-0001.jpg?ref_type=heads', 5);
INSERT INTO award (winnerName, winnerBackground, winnerSpeech, winnerImage, category_id)
VALUES ('Ivo', 'Reception', 'I drew a bike because I like bicycles. I added a Christmas hat and snowflakes. I got a bike for my birthday, it would make a good gift at Christmas. I would love it to be a Christmas light!', 'https://git.cardiff.ac.uk/c23042881/sohoawardimg/-/blob/main/.idea/2022/Reception%20Xmas%20Kids%20entries%202022%206_page-0001.jpg?ref_type=heads', 5);
INSERT INTO award (winnerName, winnerBackground, winnerSpeech, winnerImage, category_id)
VALUES ('Hikali', 'Year 5', 'When we were taking pictures I saw a croissant shop and this inspired my design. I made it colourful like the rainbow on our school logo which also reminds me of Soho. I added the holly to give it a touch of Christmas!', 'https://git.cardiff.ac.uk/c23042881/sohoawardimg/-/blob/main/.idea/2022/Year%205%20Xmas%20Kids%20entries%202022%2014_page-0001.jpg?ref_type=heads', 6);
INSERT INTO award (winnerName, winnerBackground, winnerImage, category_id)
VALUES ('Coco', 'Year 6', 'https://git.cardiff.ac.uk/c23042881/sohoawardimg/-/blob/main/.idea/2022/Year%206%20Xmas%20Kids%20entries%202022%202_page-0001.jpg?ref_type=heads', 6);
INSERT INTO award (winnerName, winnerBackground, winnerSpeech, winnerImage, category_id)
VALUES ('Daniel', 'Year 4', 'When we were going outside and taking pictures, ... I noticed that there were some local places with logos that I liked. I combined a Chinese lucky cat with a pub logo to create my unique design. | added a Santa hat to make it more Christmassy. I would be very surprised and excited to win and have my work displayed on the street.','https://git.cardiff.ac.uk/c23042881/sohoawardimg/-/blob/main/.idea/2022/Year%204%20Xmas%20Kids%20entries%202022%2011_page-0001.jpg?ref_type=heads', 7);
INSERT INTO award (winnerName, winnerBackground, winnerSpeech, winnerImage, category_id)
VALUES ('Maria', 'Year 5', 'I wanted to pick something of inspiration from inside our school. Crawford is our school dog who was in our class last year and helps for children to feel calm. It would be great to embrace him and have him brighten up the streets of Soho. | would feel very excited to have one of my designs up in public for everyone to see.','https://git.cardiff.ac.uk/c23042881/sohoawardimg/-/blob/main/.idea/2022/Year%205%20Xmas%20Kids%20entries%202022%201_page-0001.jpg?ref_type=heads', 7);
